	cls_GeomConvert_CompCurveToBSplineCurve.def("Add", [](GeomConvert_CompCurveToBSplineCurve &self, const opencascade::handle<Geom_BoundedCurve> & a0, const Standard_Real a1) -> Standard_Boolean { return self.Add(a0, a1); }, py::arg("NewCurve"), py::arg("Tolerance"));
