	cls_GeomConvert_CompCurveToBSplineCurve.def("Add", [](GeomConvert_CompCurveToBSplineCurve &self, const opencascade::handle<Geom_BoundedCurve> & a0, const Standard_Real a1, const Standard_Boolean a2, const Standard_Boolean a3) -> Standard_Boolean { return self.Add(a0, a1, a2, a3); }, py::arg("NewCurve"), py::arg("Tolerance"), py::arg("After"), py::arg("WithRatio"));
